
jQuery(document).ready(function($){var header_id=document.getElementById('header-menu');var footer_id=document.getElementById('footer-menu');if(header_id.id==="header-menu"){$('#hamburger').on('click',function(){$(`#${header_id.id}`).toggleClass('active')})}
if(footer_id.id==="footer-menu"){if(footer_id){footer_id.classList.remove("custom-menu")
footer_id.style.display="flex"
footer_id.style.flexWrap="wrap"
footer_id.style.justifyContent="space-around"
footer_id.style.alignItems="center"
footer_id.style.listStyle="none"
footer_id.style.margin='0px'
footer_id.style.padding="0px"}}});



document.addEventListener("DOMContentLoaded",function(){const links=document.querySelectorAll("#indice a");const chapters=document.querySelectorAll(".capitulo");const conteudoGrid=document.querySelector(".conteudo-grid");function hideAllChapters(){chapters.forEach(chapter=>chapter.classList.remove("ativo"))}
links.forEach(link=>{link.addEventListener("click",function(e){e.preventDefault();const chapterId=this.getAttribute("data-chapter");const targetChapter=document.getElementById(chapterId);if(targetChapter){hideAllChapters();targetChapter.classList.add("ativo");conteudoGrid.scrollIntoView({behavior:'smooth',block:'start'})}})});if(chapters.length>0){chapters[0].classList.add("ativo")}});


